import React, { useState, useEffect } from 'react';

function Stopwatch() {
  const [time, setTime] = useState(0);
  const [laps, setLaps] = useState([]);
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    let interval;
    if (isRunning) {
      interval = setInterval(() => {
        setTime(prevTime => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [isRunning]);

  const startStopwatch = () => {
    setIsRunning(true);
  };

  const stopStopwatch = () => {
    setIsRunning(false);
  };

  const resetStopwatch = () => {
    setTime(0);
    setLaps([]);
    setIsRunning(false);
  };

  const lapStopwatch = () => {
    setLaps(prevLaps => [ ...prevLaps,time]);
  };

  // Calculate average lap time
  const calculateAverageLapTime = () => {
    if (laps.length === 0) return 0;
    
    const totalLapTime = laps.reduce((acc, lapTime) =>  acc +lapTime, 0);
    const averageTime = totalLapTime / laps.length;
    const adjustedTime = averageTime * 1.1;

    return adjustedTime; 
  };

  const calculatePiecesPerHour = () => {
    const adjustedAverageLapTime = calculateAverageLapTime();
    if (adjustedAverageLapTime === 0) return 0;
    
    const piecesPerHour = 3600 / adjustedAverageLapTime; // 3600 seconds in an hour
    return piecesPerHour.toFixed(2); // returning a formatted value with 2 decimal places
  };
   
    


  return (
    <div>
      <h1>{formatTime(time)}</h1>
      <button onClick={startStopwatch}>Start</button>
      <button onClick={stopStopwatch}>Stop</button>
      <button onClick={resetStopwatch}>Reset</button>
      <button onClick={lapStopwatch}>Lap</button>
      <ul>
        {laps.map((lapTime, index) => (
          <li key={index}>{`Lap ${index + 1}: ${formatTime(lapTime)}`}</li>
        ))}
      </ul>
      <p>Average Lap Time (with 10%): {formatTime(calculateAverageLapTime())}</p>
      <p>Pieces per Hour: {calculatePiecesPerHour()}</p>
    </div>
  );
}

function formatTime(time) {
  const minutes = Math.floor(time / 60);
  const seconds = time % 60;
  return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
}

export default Stopwatch;
