import React from 'react';
import { useLocation } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './Style.css';

export default function Third() {
    const location = useLocation();
    const navigate = useNavigate();

    const data = location.state?.data || [];

    const handleDelete = (id) => {
        axios.delete(`http://localhost:8070/router/delete/${id}`)
            .then(response => {
                console.log(response.data);
                navigate('/First');
            })
            .catch(error => {
                console.error('There was an error deleting the record!', error);
            });
    };

    const handleUpdate = (item) => {
        navigate('/Update', { state: { data: item } }); 
    };


    return (
        <div class="bg-secondary">
            <h1>Search Results</h1>

            <ul>
                {data.map(item => (
                    <li key={item._id}>
                        {item.team} - {item.date} - {item.Operatorname} - {item.Operatorgrade} - {item.Operationname} - {item.Operationgrade} - {item.adjustedTime} - {item.piecesPerHour}
                        <button onClick={() => handleUpdate(item)}>Update</button>
                        <button onClick={() => handleDelete(item._id)}>Delete</button> 
                      
                    </li>
                ))}
            </ul>
       
        </div>
    );
}
