import React from 'react';
import { useLocation } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './Style.css';

export default function Third() {
    const location = useLocation();
    const navigate = useNavigate();

    const data = location.state?.data || [];

    const handleDelete = (id) => {
        axios.delete(`http://localhost:8070/router/delete/${id}`)
            .then(response => {
                console.log(response.data);
                navigate('/First');
            })
            .catch(error => {
                console.error('There was an error deleting the record!', error);
            });
    };

    const handleUpdate = (item) => {
        navigate('/Update', { state: { data: item } }); 
    };


    return (
        <div class="bg-secondary">
            <h1>Search Results</h1>

            <ul>
                {data.map(item => (
                    <li key={item._id}>
                        {item.team} - {item.date} - {item.Operatorname} - {item.Operatorgrade} - {item.Operationname} - {item.Operationgrade} - {item.adjustedTime} - {item.piecesPerHour}
                        <button onClick={() => handleUpdate(item)} class="btn btn-outline-light update">Update</button>
                        <button onClick={() => handleDelete(item._id)} class="btn btn-outline-light delete">Delete</button> 
                      
                    </li>
                ))}
            </ul>
            <table class="table table-striped">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Team</th>
      <th scope="col">Date</th>
      <th scope="col">Operatorname</th>
      <th scope="col">Operatorgrade</th>
      <th scope="col">Operationname</th>
      <th scope="col">Operatorgrade</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td>Larry</td>
      <td>the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
        </div>
    );
}
