import React, { useState } from 'react';
import { useLocation,useNavigate } from "react-router-dom";
import axios from 'axios';
import Stopwatch from '../components/Stopwatch';

export default function Update() {
    const location = useLocation();
    const navigate = useNavigate();
    const data = location.state?.data || {};
    const [team, setTeam] = useState(data.team || '');
    const [date, setDate] = useState(data.date || '');
    const [op1, setOp1] = useState(data.Operatorname || '');
    const [grade1, setGrade1] = useState(data.Operatorgrade || '');
    const [op2, setOp2] = useState(data.Operationname || '');
    const [grade2, setGrade2] = useState(data.Operationgrade || '');
    const [stopwatch1, setStopwatch1] = useState(0);
    const [stopwatch2, setStopwatch2] = useState(0);
    

    const handleStopwatch1Change = (value) => {
      setStopwatch1(value);
      setStopwatch2(value);
  };

    const handleUpdate = (event) => {
        event.preventDefault();
        /*const stopwatch1 = value;
        if (isNaN(stopwatch1)) {
            console.error('Value must be a number');
            return;
        }*/
        axios.put(`http://localhost:8070/router/update/${data._id}`, {
            team,
            date,
            Operatorname: op1,
            Operatorgrade: grade1,
            Operationname: op2,
            Operationgrade: grade2,
            value: stopwatch1,
            pieces: stopwatch2 
        })
        .then(response => {
            console.log(response.data);
            navigate('/Third');
        })
        .catch(error => {
            console.error('There was an error!', error);
        });
    };

    return (
        <div>
            <h1>Update details</h1>
            <form onSubmit={()=>{
                handleUpdate();
                handleStopwatch1Change();
            }}>
                <label>Team</label>
                <input type="text" name="team" value={team} onChange={(e) => setTeam(e.target.value)} /><br/>

                <label>Date</label>
                <input type="date" name="date" value={date} onChange={(e) => setDate(e.target.value)} /><br/>
                
                <label>Operation</label>
                <input type="text" name="op1" value={op1} onChange={(e) => setOp1(e.target.value)} />
                
                <label>Grade</label>
                <input type="text" name="grade1" value={grade1} onChange={(e) => setGrade1(e.target.value)} /><br/>
                
                <label>Operator</label>
                <input type="text" name="op2" value={op2} onChange={(e) => setOp2(e.target.value)} />
                
                <label>Grade</label>
                <input type="text" name="grade2" value={grade2} onChange={(e) => setGrade2(e.target.value)} /><br/>

                
                <button type="submit">Update</button>
            </form>
            <Stopwatch/>
        </div>
    );
}
